package week1.day1;

public class Assignment_ConvertPosToNeg {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number = -40;
		int result = 0;
		if (number < 0) {
			result = number * -1;
		}
		System.out.println("The Given Number " + number + " is converted in to " + result);
	}

}
